hat_t1_f=[-1,1,0;0,1,0;1,0,0;1,0,0];
hat_t2_f=[-1,0,1;0,0,1;0,0,1;0,1,0];

t1_f=[tau(4,:);tau(2,:);tau(1,:);tau(1,:)];
t2_f=[tau(5,:);tau(3,:);tau(3,:);tau(2,:)];

S=zeros(4,1);
F=zeros(2,3,4);
for k=1:4
hat_t1=(hat_t1_f(k,:))';
hat_t2=(hat_t2_f(k,:))';  

Binv_hat_t1=C'*(hat_t1/norm(hat_t1,2));
Binv_hat_t2=C'*(hat_t2/norm(hat_t2,2));


t1=(t1_f(k,:))'/norm(t1_f(k,:),2);
t2=(t2_f(k,:))'/norm(t2_f(k,:),2);
nn=cross(t1,t2);
S(k)=norm(cross(B*hat_t1,B*hat_t2),2);

nn=nn/norm(nn,2);
v1=[t1,t2,nn]\Binv_hat_t1;
v2=[t1,t2,nn]\Binv_hat_t2;

F(:,:,k)=[v1.';v2.'];

end

D=blkdiag(C,C,C,C,...
          C,C,C,C,C,C,...
          C,C,C,C,C,C,...
          F(:,:,1),F(:,:,2),F(:,:,3),F(:,:,4),...
          eye(22)/detB);
D=D*detB;





E=zeros(82,78);
E(1:48,1:48)=eye(48);
E([49,50,52,53,55,56,58,59],49:56)=eye(8);

E(51,[66,69,72])=...
    [1,-1,1]/S(1);

E(54,[60,63,72])=...
    [1,-1,1]/S(2);

E(57,[57,63,69])=...
    [1,-1,1]/S(3);

E(60,[57,60,66])=...
    [1,-1,1]/S(4);

E(61:82,57:78)=eye(22);
C=D*E;







end
end


